[asy]
unitsize(0.4 cm);

pair A, B, C;
pair[] F;
real a, b, c, s;

a = 5;
b = sqrt(3)/2*5;
c = 5/2;
s = 8;

A = (-s/2,-sqrt(3)/2*(s - 5));
B = (0,b);
C = (s/2,-sqrt(3)/2*(s - 5));
F[1] = (c,0);
F[2] = (-c,0);

draw(yscale(b)*xscale(a)*Circle((0,0),1));
draw(A--B--C--cycle);

label("$P$", A, SW);
label("$Q$", B, N);
label("$R$", C, SE);
dot("$F_1$", F[1], NE);
dot("$F_2$", F[2], NW);
[/asy]
